import React from 'react';

export default function RecipeCard({
  title,
  calories,
  image,
  ingredients,
  url,
}) {
  return (
    <div>
      <h1>{title}</h1>
      <img src={image} alt="recipe" />
      <ul>
        {' '}
        Ingredients:
        {ingredients.map((ingredient) => (
          <li>{ingredient.text}</li>
        ))}
      </ul>
      <p> Calories: {calories}</p>

      <a class="recipeLabel" href={url}>
        View Full Recipe
      </a>
    </div>
  );
}
